/**
 * Defines link mode: No links (definges logic connection), Single-side connection or double connection
 */
export enum LinkingMode {
    /**
     * No links/connections
     */
    NoLinks = 0,
    /**
     * Single side linked connection
     */
    SingleLink = 1,
    /**
     * Double side linked connection
     */
    DoubleLink = 2
}
