/**
 * The implementation of the default ui serializer registry.
 * It used by autogenerated UI to get/apply properties to any Node.
 */
export class UIRegistry {
    /**
     * The implementation of the default ui serializer registry.
     * It used by autogenerated UI to get/apply properties to any Node.
     */
    constructor();
    /**
     * Return instance of the default UIRegistry
     */
    static getInstance(): UIRegistry;
    /**
     * Add serializer
     * @param type type of the serializer
     * @param serializer serializer
     */
    addSerializer(type: string, serializer: any): this;
    /**
     * Remove serializer
     * @param type type of the serializer
     */
    removeSerializer(type: string): this;
    /**
     * Return serializer for the specified type
     * @param type serializer for the specified type
     */
    getSerializer(type: any): any;
}
