/**
 * Enum of paper scaling options:
 *     - AsIs : 'AsIs'
 *     - FitWidth : 'FitWidth'
 *     - FitHeight : 'FitHeight'
 *     - FitBoth : 'FitBoth'
 *     - Custom : 'Custom'
 */
export enum ScalingOptions {
    /**
     * As is
     */
    AsIs = 'AsIs',
    /**
     * Fit width
     */
    FitWidth = 'FitWidth',
    /**
     * Fit height
     */
    FitHeight = 'FitHeight',
    /**
     * Fit both
     */
    FitBoth = 'FitBoth',
    /**
     * Custom
     */
    Custom = 'Custom'
}
