/**
 * Enum of label location types
 */
export enum LocationType {
    /**
     * Labels on the left side
     */
    Left = 1,
    /**
     * Labels on the right side
     */
    Right = 2,
    /**
     * Labels on the labeled elements
     */
    Center = 4,
    /**
     * Labels on the left or right side (for along trajectory mode)
     */
    Auto = 16
}
