/**
 * Data format type enum
 */
export enum DataFormatType {
    /**
     * Undefined
     */
    Undefined = 0,
    /**
     * Byte
     */
    Byte = 1,
    /**
     * Short
     */
    Short = 2,
    /**
     * Integer
     */
    Int = 3,
    /**
     * Float
     */
    Float = 4,
    /**
     * IBM Float
     */
    IbmFloat = 5,
    /**
     * Double
     */
    Double = 6,
    /**
     * Nibble
     */
    Nibble = 7,
    /**
     * Unsigned byte
     */
    UByte = 8,
    /**
     * Unsigned short
     */
    UShort = 9,
    /**
     * Unsigned integer 32 bit (The same as UInt32)
     */
    UInt = 10,
    /**
     * Unsigned integer 32 bit
     */
    UInt32 = 11,
    /**
     * Unsigned integer 64 bit
     */
    UInt64 = 12,
    /**
     * Integer 32 bit (The same as Int)
     */
    Int32 = 13,
    /**
     * Integer 64-bit
     */
    Int64 = 14
}
