/**
 * Helper class for the Date
 */
export class DateUtil {
    /**
     * Helper class for the Date
     */
    constructor();
    /**
     * Format date
     * @param date date instance
     * @param format date format
     */
    static format(date: Date, format: string): string;
    /**
     * Format UTC date
     * @param date date instance
     * @param format date format
     */
    static formatUTC(date: Date, format: string): string;
}
